import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import LoginPage from './components/pages/LoginPage';
import MenuPage from './components/pages/MenuPage';
import OutingRegisterPage from './components/pages/OutingRegisterPage';
import { Student, OutingRequest, StayRequest } from './types/index';
import './App.css';

const App: React.FC = () => {
  const [currentPage, setCurrentPage] = useState<string>('login');
  const [currentStudent, setCurrentStudent] = useState<Student | null>(null);
  const [outingRequests, setOutingRequests] = useState<OutingRequest[]>([]);
  const [stayRequests, setStayRequests] = useState<StayRequest[]>([]);

  const renderCurrentPage = () => {
    const props = {
      currentStudent,
      setCurrentStudent,
      setCurrentPage,
      outingRequests,
      setOutingRequests,
      stayRequests,
      setStayRequests
    };

    switch (currentPage) {
      case 'login':
        return <LoginPage {...props} />;
      case 'menu':
        return <MenuPage {...props} />;
      case 'register':
        return <OutingRegisterPage {...props} />;
      default:
        return <LoginPage {...props} />;
    }
  };

  return (
    <div className="app">
      {renderCurrentPage()}
    </div>
  );
};

export default App;
