import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('login');
  const [currentStudent, setCurrentStudent] = useState(null);
  const [outingRequests, setOutingRequests] = useState([]);
  const [stayRequests, setStayRequests] = useState([]);

  const renderCurrentPage = () => {
    const props = {
      currentStudent,
      setCurrentStudent,
      setCurrentPage,
      outingRequests,
      setOutingRequests,
      stayRequests,
      setStayRequests
    };

    switch (currentPage) {
      case 'login':
        return <div>로그인 페이지</div>;
      case 'menu':
        return <div>메뉴 페이지</div>;
      case 'register':
        return <div>외출 신청 페이지</div>;
      default:
        return <div>로그인 페이지</div>;
    }
  };

  return (
    <div className="app">
      {renderCurrentPage()}
    </div>
  );
};

export default App;
