import React, { useState } from 'react';
import { Student, StayRequest } from '../../types';
import axios from 'axios';

interface StayReturnPageProps {
  currentStudent: Student | null;
  stayRequests: StayRequest[];
  setStayRequests: React.Dispatch<React.SetStateAction<StayRequest[]>>;
  setCurrentPage: (page: string) => void;
}

const StayReturnPage: React.FC<StayReturnPageProps> = ({ 
  currentStudent, 
  stayRequests, 
  setStayRequests, 
  setCurrentPage 
}) => {
    const pendingRequests = stayRequests.filter(
      req => req.studentId === currentStudent?.Student_ID && req.status !== 'completed'
    );

    const handleReturn = async (request: StayRequest) => {
      try {
        const requestData = {
          sleepOut_dt: request.date,
          seq: request.seq,
          returnType: 'NORMAL',
          returnNote: ''
        };

        await axios.post('http://localhost:3001/api/stay/return', requestData);

        setStayRequests(prev => 
          prev.map(req => 
            req.id === request.id 
              ? { 
                  ...req, 
                  status: 'completed' as const,
                  actualReturnDate: request.returnDate,
                  actualReturnTime: request.returnTime
                }
              : req
          )
        );

        alert('외박 복귀가 완료되었습니다.');
        setCurrentPage('menu');
      } catch (error) {
        console.error('외박 복귀 처리 실패:', error);
        alert('외박 복귀 처리 중 오류가 발생했습니다.');
      }
    };

    return (
      <div className="container-fluid vh-100 bg-light">
        <div className="bg-info text-white p-3 d-flex align-items-center">
          <button 
            className="btn btn-outline-light me-3"
            onClick={() => setCurrentPage('menu')}
          >
            ←
          </button>
          <h4 className="mb-0">외박 복귀</h4>
        </div>

        <div className="p-4">
          <div className="card mb-3">
            <div className="card-body">
              <h6 className="card-title">학생 정보</h6>
              <p className="mb-0">{currentStudent?.Student_Name} ({currentStudent?.Grade}학년)</p>
            </div>
          </div>

          {pendingRequests.length === 0 ? (
            <div className="text-center py-5">
              <div className="fs-1 mb-3">
                <span role="img" aria-label="달">🌙</span>
              </div>
              <h5>복귀 처리할 외박 신청이 없습니다</h5>
              <p className="text-muted">먼저 외박을 신청해주세요.</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead className="table-light">
                  <tr>
                    <th>외박일자</th>
                    <th>복귀일자</th>
                    <th>외박사유</th>
                    <th>처리</th>
                  </tr>
                </thead>
                <tbody>
                  {pendingRequests.map(req => (
                    <tr key={req.id}>
                      <td>{req.date}</td>
                      <td>{req.returnDate}</td>
                      <td>{req.reason}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-info"
                          onClick={() => handleReturn(req)}
                        >
                          복귀처리
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    );
  };

export default StayReturnPage;
