import React, { useState, useEffect } from 'react';
import { Student, StayRequest, ServerStayRequest } from '../../types';
import axios from 'axios';

interface StayReturnPageProps {
  currentStudent: Student | null;
  stayRequests: StayRequest[];
  setStayRequests: React.Dispatch<React.SetStateAction<StayRequest[]>>;
  setCurrentPage: (page: string) => void;
}

const StayReturnPage: React.FC<StayReturnPageProps> = ({ 
  currentStudent, 
  stayRequests, 
  setStayRequests, 
  setCurrentPage 
}) => {
    // Ïô∏Î∞ï Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
    useEffect(() => {
      const fetchStayList = async () => {
        if (!currentStudent?.Student_ID) {
          console.log('ÌïôÏÉù Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.');
          return;
        }

        try {
          console.log('Ïô∏Î∞ï Î™©Î°ù Ï°∞Ìöå ÏãúÏûë:', currentStudent.Student_ID);
          const response = await axios.get(`http://localhost:3001/api/stay/list/${currentStudent.Student_ID}`);
          console.log('Ïô∏Î∞ï Î™©Î°ù Ï°∞Ìöå Í≤∞Í≥º:', response.data);
          setStayRequests(response.data);
        } catch (error) {
          console.error('Ïô∏Î∞ï Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
        }
      };

      console.log('useEffect Ïã§Ìñâ, currentStudent:', currentStudent);
      fetchStayList();
    }, [currentStudent, setStayRequests]);

    console.log('Ï†ÑÏ≤¥ stayRequests:', stayRequests);
    const pendingRequests = (stayRequests as unknown as ServerStayRequest[]).map(req => ({
      id: req.Seq.toString(),
      studentId: currentStudent?.Student_ID || '',
      date: req.SleepOut_Dt,
      time: '',
      returnDate: req.Return_Date,
      returnTime: '',
      reason: req.SleepOut_Reason,
      otherReason: '',
      status: 'pending',
      actualReturnDate: undefined,
      actualReturnTime: undefined,
      note: '',
      seq: req.Seq
    })).filter(req => {
      // Return_DateÍ∞Ä Ïò§Îäò Ïù¥ÌõÑÏù∏ Í≤ΩÏö∞Îßå ÌëúÏãú
      const today = new Date().toISOString().split('T')[0];
      return req.returnDate >= today;
    });
    console.log('ÌïÑÌÑ∞ÎßÅÎêú pendingRequests:', pendingRequests);

    const handleReturn = async (request: StayRequest) => {
      try {
        const requestData = {
          sleepOut_dt: request.date,
          seq: request.seq,
          returnType: 'NORMAL',
          returnNote: ''
        };

        await axios.post('http://localhost:3001/api/stay/return', requestData);

        setStayRequests(prev => 
          prev.map(req => 
            req.id === request.id 
              ? { 
                  ...req, 
                  status: 'completed' as const,
                  actualReturnDate: request.returnDate,
                  actualReturnTime: request.returnTime
                }
              : req
          )
        );

        alert('Ïô∏Î∞ï Î≥µÍ∑ÄÍ∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.');
        setCurrentPage('menu');
      } catch (error) {
        console.error('Ïô∏Î∞ï Î≥µÍ∑Ä Ï≤òÎ¶¨ Ïã§Ìå®:', error);
        alert('Ïô∏Î∞ï Î≥µÍ∑Ä Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    };

    return (
      <div className="container-fluid vh-100 bg-light">
        <div className="bg-info text-white p-3 d-flex align-items-center">
          <button 
            className="btn btn-outline-light me-3"
            onClick={() => setCurrentPage('menu')}
          >
            ‚Üê
          </button>
          <h4 className="mb-0">Ïô∏Î∞ï Î≥µÍ∑Ä</h4>
        </div>

        <div className="p-4">
          <div className="card mb-3">
            <div className="card-body">
              <h6 className="card-title">ÌïôÏÉù Ï†ïÎ≥¥</h6>
              <p className="mb-0">{currentStudent?.Student_Name} ({currentStudent?.Grade}ÌïôÎÖÑ)</p>
            </div>
          </div>

          {pendingRequests.length === 0 ? (
            <div className="text-center py-5">
              <div className="fs-1 mb-3">
                <span role="img" aria-label="Îã¨">üåô</span>
              </div>
              <h5>Î≥µÍ∑Ä Ï≤òÎ¶¨Ìï† Ïô∏Î∞ï Ïã†Ï≤≠Ïù¥ ÏóÜÏäµÎãàÎã§</h5>
              <p className="text-muted">Î®ºÏ†Ä Ïô∏Î∞ïÏùÑ Ïã†Ï≤≠Ìï¥Ï£ºÏÑ∏Ïöî.</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead className="table-light">
                  <tr>
                    <th>Ïô∏Î∞ïÏùºÏûê</th>
                    <th>Î≥µÍ∑ÄÏùºÏûê</th>
                    <th>Ïô∏Î∞ïÏÇ¨Ïú†</th>
                    <th>Ï≤òÎ¶¨</th>
                  </tr>
                </thead>
                <tbody>
                  {pendingRequests.map(req => (
                    <tr key={req.id}>
                      <td>{req.date}</td>
                      <td>{req.returnDate}</td>
                      <td>{req.reason}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-info"
                          onClick={() => handleReturn(req)}
                        >
                          Î≥µÍ∑ÄÏ≤òÎ¶¨
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    );
  };

export default StayReturnPage;
