import React, { useState } from 'react';
import { Student, StayRequest } from '../../types';
import axios from 'axios';

interface StayReturnPageProps {
  currentStudent: Student | null;
  stayRequests: StayRequest[];
  setStayRequests: React.Dispatch<React.SetStateAction<StayRequest[]>>;
  setCurrentPage: (page: string) => void;
}

const StayReturnPage: React.FC<StayReturnPageProps> = ({ 
  currentStudent, 
  stayRequests, 
  setStayRequests, 
  setCurrentPage 
}) => {
    const [selectedRequest, setSelectedRequest] = useState<string>('');
    const [returnDate, setReturnDate] = useState('');
    const [returnTime, setReturnTime] = useState('');
    const [note, setNote] = useState('');

    const pendingRequests = stayRequests.filter(
      req => req.studentId === currentStudent?.Student_ID && req.status !== 'completed'
    );

    const handleReturn = async () => {
      if (!selectedRequest || !returnDate || !returnTime) {
        alert('모든 필수 항목을 입력해주세요.');
        return;
      }

      try {
        const selectedStay = stayRequests.find(req => req.id === selectedRequest);
        if (!selectedStay) {
          alert('선택된 외박 신청을 찾을 수 없습니다.');
          return;
        }

        const requestData = {
          studentId: currentStudent?.Student_ID,
          date: selectedStay.date,
          time: selectedStay.time,
          returnDate: returnDate,
          returnTime: returnTime,
          note: note
        };

        await axios.post('http://localhost:3001/api/stay/return', requestData);

        setStayRequests(prev => 
          prev.map(req => 
            req.id === selectedRequest 
              ? { 
                  ...req, 
                  status: 'completed' as const, 
                  actualReturnDate: returnDate,
                  actualReturnTime: returnTime,
                  note 
                }
              : req
          )
        );

        alert('외박 복귀가 완료되었습니다.');
        setCurrentPage('menu');
      } catch (error) {
        console.error('외박 복귀 처리 실패:', error);
        alert('외박 복귀 처리 중 오류가 발생했습니다.');
      }
    };

    return (
      <div className="container-fluid vh-100 bg-light">
        <div className="bg-info text-white p-3 d-flex align-items-center">
          <button 
            className="btn btn-outline-light me-3"
            onClick={() => setCurrentPage('menu')}
          >
            ←
          </button>
          <h4 className="mb-0">외박 복귀</h4>
        </div>

        <div className="p-4">
          {pendingRequests.length === 0 ? (
            <div className="text-center py-5">
              <div className="fs-1 mb-3">
                <span role="img" aria-label="달">🌙</span>
              </div>
              <h5>복귀 처리할 외박 신청이 없습니다</h5>
              <p className="text-muted">먼저 외박을 신청해주세요.</p>
            </div>
          ) : (
            <>
              <div className="table-responsive">
                <table className="table table-hover">
                  <thead className="table-light">
                    <tr>
                      <th>선택</th>
                      <th>외박일자</th>
                      <th>외박시간</th>
                      <th>복귀예정일자</th>
                      <th>복귀예정시간</th>
                      <th>외박사유</th>
                      <th>기타사유</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pendingRequests.map(req => (
                      <tr key={req.id}>
                        <td>
                          <input
                            type="radio"
                            name="selectedRequest"
                            value={req.id}
                            checked={selectedRequest === req.id}
                            onChange={(e) => setSelectedRequest(e.target.value)}
                            className="form-check-input"
                          />
                        </td>
                        <td>{req.date}</td>
                        <td>{req.time}</td>
                        <td>{req.returnDate}</td>
                        <td>{req.returnTime}</td>
                        <td>{req.reason}</td>
                        <td>{req.otherReason}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              <div className="row g-3 mt-3">
                <div className="col-6">
                  <label className="form-label fw-bold">복귀일자 *</label>
                  <input
                    type="date"
                    className="form-control"
                    value={returnDate}
                    onChange={(e) => setReturnDate(e.target.value)}
                  />
                </div>

                <div className="col-6">
                  <label className="form-label fw-bold">복귀시간 *</label>
                  <input
                    type="time"
                    className="form-control"
                    value={returnTime}
                    onChange={(e) => setReturnTime(e.target.value)}
                  />
                </div>

                <div className="col-12">
                  <label className="form-label fw-bold">비고</label>
                  <textarea
                    className="form-control"
                    rows={3}
                    placeholder="특이사항이 있으면 입력해주세요"
                    value={note}
                    onChange={(e) => setNote(e.target.value)}
                  />
                </div>

                <div className="col-12 mt-4">
                  <button
                    className="btn btn-info btn-lg w-100"
                    onClick={handleReturn}
                    disabled={!selectedRequest}
                  >
                    복귀 처리
                  </button>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    );
  };

export default StayReturnPage;
