import React, { useState, Dispatch, SetStateAction } from 'react';
import { Student, OutingRequest, OUTING_REASONS } from '../../types/index';
import axios from 'axios';

interface OutingRegisterPageProps {
  currentStudent: Student;
  setCurrentPage: Dispatch<SetStateAction<string>>;
  outingRequests: OutingRequest[];
  setOutingRequests: Dispatch<SetStateAction<OutingRequest[]>>;
}

const OutingRegisterPage: React.FC<OutingRegisterPageProps> = ({
  currentStudent,
  setCurrentPage,
  outingRequests,
  setOutingRequests
}) => {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [returnTime, setReturnTime] = useState('');
  const [reason1, setReason1] = useState('');
  const [reason2, setReason2] = useState('');
  const [otherReason, setOtherReason] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const newRequest: OutingRequest = {
        id: Date.now().toString(),
        studentId: currentStudent.Student_ID,
        date,
        time,
        returnTime,
        reason1,
        reason2: reason1 === '기타' ? otherReason : reason2,
        status: 'pending',
        actualReturnTime: null,
        seq: outingRequests.length + 1
      };

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/outing-requests`,
        newRequest
      );

      if (response.status === 201) {
        setOutingRequests([...outingRequests, response.data]);
        alert('외출 신청이 완료되었습니다.');
        setCurrentPage('menu');
      }
    } catch (error) {
      console.error('외출 신청 중 오류:', error);
      alert('외출 신청 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container-fluid vh-100 d-flex align-items-center justify-content-center bg-light">
      <div className="card shadow-lg" style={{ maxWidth: '600px', width: '90%' }}>
        <div className="card-header bg-white text-center py-4">
          <h2 className="text-primary mb-0">
            <span role="img" aria-label="외출">🚶</span> 외출 신청
          </h2>
          <p className="text-muted mt-2">{currentStudent.Student_Name} 학생</p>
        </div>
        <div className="card-body p-4">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label fw-bold">날짜</label>
              <input
                type="date"
                className="form-control form-control-lg"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label fw-bold">출발 시간</label>
              <input
                type="time"
                className="form-control form-control-lg"
                value={time}
                onChange={(e) => setTime(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label fw-bold">귀가 시간</label>
              <input
                type="time"
                className="form-control form-control-lg"
                value={returnTime}
                onChange={(e) => setReturnTime(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label fw-bold">외출 사유</label>
              <select
                className="form-select form-select-lg"
                value={reason1}
                onChange={(e) => setReason1(e.target.value)}
                required
              >
                <option value="">선택하세요</option>
                {OUTING_REASONS.map((reason) => (
                  <option key={reason} value={reason}>
                    {reason}
                  </option>
                ))}
              </select>
            </div>
            {reason1 === '기타' && (
              <div className="mb-3">
                <label className="form-label fw-bold">상세 사유</label>
                <input
                  type="text"
                  className="form-control form-control-lg"
                  value={otherReason}
                  onChange={(e) => setOtherReason(e.target.value)}
                  required
                />
              </div>
            )}
            {reason1 !== '기타' && reason1 && (
              <div className="mb-3">
                <label className="form-label fw-bold">상세 사유</label>
                <input
                  type="text"
                  className="form-control form-control-lg"
                  value={reason2}
                  onChange={(e) => setReason2(e.target.value)}
                  required
                />
              </div>
            )}
            <div className="d-grid gap-2">
              <button
                type="submit"
                className="btn btn-primary btn-lg"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    신청 중...
                  </>
                ) : (
                  '신청하기'
                )}
              </button>
              <button
                type="button"
                className="btn btn-outline-secondary btn-lg"
                onClick={() => setCurrentPage('menu')}
              >
                취소
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default OutingRegisterPage;
