      { id: 'return', title: 'Ïô∏Ï∂ú Î≥µÍ∑Ä', icon: 'üè†', label: 'Ïßë', color: 'success' },
      { id: 'stay-register', title: 'Ïô∏Î∞ï Ïã†Ï≤≠', icon: 'üåô', label: 'Îã¨', color: 'warning' },
      { id: 'stay-return', title: 'Ïô∏Î∞ï Î≥µÍ∑Ä', icon: '‚òÄÔ∏è', label: 'ÌÉúÏñë', color: 'info' }
    ];

    return (
      <div className="container-fluid vh-100 bg-light">
        <div className="row h-100">
          <div className="col-12">
            <div className="bg-primary text-white p-4 text-center">
              <h3 className="mb-1">ÏïàÎÖïÌïòÏÑ∏Ïöî, {currentStudent?.Student_Name}Îãò</h3>
              <p className="mb-0 opacity-75">{currentStudent?.Grade}ÌïôÎÖÑ</p>
            </div>

            <div className="p-4">
              <div className="row g-3">
                {menuItems.map(item => (
                  <div key={item.id} className="col-6">
                    <button
                      className={`btn btn-${item.color} w-100 h-100 py-4 border-0 shadow-sm`}
                      style={{ minHeight: '120px' }}
