import React, { useState } from 'react';
import { Student } from '../../types';
import axios from 'axios';

interface LoginPageProps {
  setCurrentStudent: (student: Student) => void;
  setCurrentPage: (page: string) => void;
}

interface StudentNameResponse {
  Student_ID: string;
  Student_Name: string;
  Grade: number;
  Pwd: string;
}

const LoginPage: React.FC<LoginPageProps> = ({ setCurrentStudent, setCurrentPage }) => {
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [studentName, setStudentName] = useState('');
    const [studentInfo, setStudentInfo] = useState<StudentNameResponse | null>(null);

    const handlePhoneChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
      const value = e.target.value.replace(/[^0-9]/g, '');
      console.log('ÏûÖÎ†•Îêú Í∞í:', value);
      console.log('ÏûÖÎ†•Îêú Í∞íÏùò Í∏∏Ïù¥:', value.length);

      const formattedValue = value.length > 0 ? `010-${value.slice(0, 4)}${value.length > 4 ? '-' + value.slice(4, 8) : ''}` : '';
      setPhone(value);

      if (value.length === 8) {
        console.log('Ï†ÑÌôîÎ≤àÌò∏ 8ÏûêÎ¶¨ ÏûÖÎ†•Îê®. API Ìò∏Ï∂ú Ï§ÄÎπÑ...');
        console.log('Ìè¨Îß∑Îêú Ï†ÑÌôîÎ≤àÌò∏:', formattedValue);
        alert('Ï†ÑÌôîÎ≤àÌò∏ 8ÏûêÎ¶¨ ÏûÖÎ†•Îê®. API Ìò∏Ï∂ú Ï§ÄÎπÑ...');
        setLoading(true);
        try {
          alert('API Ìò∏Ï∂ú ÏãúÏûë');
          console.log('API Ìò∏Ï∂ú ÏãúÏûë');
          const apiUrl = `${process.env.REACT_APP_API_URL}/api/student/name/${phone}`;
          console.log('API ÏöîÏ≤≠ URL:', apiUrl);

          const response = await axios.get<StudentNameResponse>(apiUrl, {
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            }
          });

          console.log('API ÏùëÎãµ:', response.data);
          setStudentName(response.data.Student_Name);
          setStudentInfo(response.data);
          alert('--- API Ìò∏Ï∂ú ÏÑ±Í≥µ ---');
        } catch (error: unknown) {
          console.error('API Ìò∏Ï∂ú ÏóêÎü¨:', error);
          if (axios.isAxiosError(error)) {
            console.error('ÏóêÎü¨ ÏÉÅÌÉú:', error.response?.status);
            console.error('ÏóêÎü¨ Îç∞Ïù¥ÌÑ∞:', error.response?.data);
            console.error('ÏóêÎü¨ Î©îÏãúÏßÄ:', error.message);
            alert(`API Ìò∏Ï∂ú Ïã§Ìå®: ${error.response?.data?.message || error.message}`);
          } else {
            console.error('Ïïå Ïàò ÏóÜÎäî ÏóêÎü¨:', error);
            alert('Ïïå Ïàò ÏóÜÎäî Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
          }

          setStudentName('');
          setStudentInfo(null);

          if (error.response?.status === 404) {
            alert('Îì±Î°ùÎêú Ìï∏ÎìúÌè∞ Î≤àÌò∏Í∞Ä ÏóÜÏäµÎãàÎã§~~');
          } else {
            alert('ÌïôÏÉù Ï†ïÎ≥¥ Ï°∞Ìöå Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
          }
          alert('--- API Ìò∏Ï∂ú Ïò§Î•ò Ï¢ÖÎ£å ---');
        } finally {
          setLoading(false);
          alert('--- handlePhoneChange finally Î∏îÎ°ù Ï¢ÖÎ£å ---');
        }
      } else {
        setStudentName('');
        setStudentInfo(null);
      }
    };

    const handleSubmit = async (e: React.FormEvent) => {
      e.preventDefault();
      alert('--- handleSubmit Ìï®Ïàò Ìò∏Ï∂úÎê® ---');

      if (!phone || phone.length !== 8) {
        alert('Ïò¨Î∞îÎ•∏ Ï†ÑÌôîÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        alert('Î°úÍ∑∏Ïù∏ Ïã§Ìå®: Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏùÄ Ï†ÑÌôîÎ≤àÌò∏');
        return;
      }

      if (!password) {
        alert('ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        alert('Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ÎπÑÎ∞ÄÎ≤àÌò∏ ÎØ∏ÏûÖÎ†•');
        return;
      }

      if (!studentInfo) {
        alert('Ï†ÑÌôîÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÍ≥† ÌïôÏÉù Ï†ïÎ≥¥Î•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        alert('Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ÌïôÏÉù Ï†ïÎ≥¥ ÏóÜÏùå');
        return;
      }

      setLoading(true);
      alert('Î°úÍ∑∏Ïù∏ ÏãúÎèÑ...');

      try {
        // ÎπÑÎ∞ÄÎ≤àÌò∏ ÎπÑÍµê
        if (password !== studentInfo.Pwd) {
          alert('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
          alert('Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ÎπÑÎ∞ÄÎ≤àÌò∏ Î∂àÏùºÏπò');
          return;
        }

        // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ Ïãú studentInfoÎ•º ÏÇ¨Ïö©
        const studentData: Student = {
          Student_ID: studentInfo.Student_ID,
          Student_Name: studentInfo.Student_Name,
          Grade: studentInfo.Grade.toString(),
          Pwd: studentInfo.Pwd,
          phone: phone,
          class: '',  // Í∏∞Î≥∏Í∞íÏúºÎ°ú Îπà Î¨∏ÏûêÏó¥ ÏÑ§Ï†ï
          number: ''  // Í∏∞Î≥∏Í∞íÏúºÎ°ú Îπà Î¨∏ÏûêÏó¥ ÏÑ§Ï†ï
        };

        setCurrentStudent(studentData);
        setCurrentPage('menu');
        alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!' + JSON.stringify(studentData));
      } catch (error: any) {
        alert('!!! Î°úÍ∑∏Ïù∏ Ïò§Î•ò Î∞úÏÉù !!!');
        alert('Ïò§Î•ò Í∞ùÏ≤¥: ' + JSON.stringify(error));
        alert('Î°úÍ∑∏Ïù∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      } finally {
        setLoading(false);
        alert('--- handleSubmit finally Î∏îÎ°ù Ï¢ÖÎ£å ---');
      }
      alert('--- handleSubmit Ìï®Ïàò Ï¢ÖÎ£å ---');
    };

    return (
      <div className="container-fluid vh-100 d-flex align-items-center justify-content-center bg-primary">
        <div className="card shadow-lg" style={{ maxWidth: '400px', width: '90%' }}>
          <div className="card-header bg-white text-center py-4">
            <h2 className="text-primary mb-0">
              <span role="img" aria-label="ÌïôÍµê">üè´</span> SBON
            </h2>
            <p className="text-muted mt-2">Ïô∏Ï∂ú/Ïô∏Î∞ï Ïã†Ï≤≠ ÏãúÏä§ÌÖú</p>
          </div>
          <div className="card-body p-4">
            <div className="mb-3">
              <label className="form-label fw-bold">Ï†ÑÌôîÎ≤àÌò∏</label>
              <div className="d-flex align-items-center gap-2">
                <div className="input-group">
                  <span className="input-group-text" style={{ fontSize: '1rem' }}>010-</span>
                  <input
                    type="tel"
                    className="form-control form-control-lg"
                    placeholder="0000-0000"
                    value={phone.length > 0 ? `${phone.slice(0, 4)}${phone.length > 4 ? '-' + phone.slice(4, 8) : ''}` : ''}
                    onChange={handlePhoneChange}
                    onKeyDown={(e) => e.key === 'Enter' && handleSubmit(e)}
                    maxLength={9}
                  />
                </div>
                {loading ? (
                  <span className="spinner-border spinner-border-sm text-primary"></span>
                ) : studentName && (
                  <span className="badge bg-info text-white p-2" style={{ fontSize: '1rem' }}>
                    {studentName}
                  </span>
                )}
              </div>
            </div>
            <div className="mb-3">
              <label className="form-label fw-bold">ÎπÑÎ∞ÄÎ≤àÌò∏</label>
              <input
                type="password"
                className="form-control form-control-lg"
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleSubmit(e)}
              />
            </div>
            <button
              className="btn btn-primary btn-lg w-100"
              onClick={handleSubmit}
              disabled={loading}
            >
